","jsContent":"(function () {

    var orderId = OpfContext.orderId;
    var referenceId = OpfContext.referenceId;
    var amountInExponent = OpfContext.amountInExponent;
    var currency = OpfContext.currency;
    var resultURL = OpfContext.resultURL;
    var language = OpfContext.contentLanguage;

    const paymentMethodsResponse = JSON.parse(OpfContext.customFields.responseBody);


    const languageMap = {
        "ja": "ja-JP",    // Japanese to Japanese (Japan)
        "en": "en-US",    // English to English (United States)
        "zh": "zh-CN",    // Simplified Chinese (China) or you can map to zh-TW based on your situation
        "fr": "fr-FR",    // French to French (France)
        "de": "de-DE",    // German to German (Germany)
        "es": "es-ES"     // Spanish to Spanish (Spain)
        // more ,please refer to https://github.com/Adyen/adyen-web/tree/main/packages/server/translations
    };
    var contentLanguage = languageMap[language] || language;
    console.log('contentLanguage: ', contentLanguage);

    var countryCode = OpfContext.billingAddress.country;


    var clientKey = OpfContext.vars.clientKey;
    var environment = OpfContext.vars.mode;

    const hasContent = v => v != null && v.trim() !== '';
    const hasDelivery = (hasContent(OpfContext.shippingAddress.country) && hasContent(OpfContext.shippingAddress.city) && hasContent(OpfContext.shippingAddress.postalCode)) ?? false;

    var storePaymentMethod = false;
    var storedPaymentMethodId = false; //decide the shopper interactionvalue

    const { AdyenCheckout, Card, ApplePay } = window.AdyenWeb;


    //Flag to prevent duplicate submissions
    var isSubmitting = false;


    function submitSuccess(response, actions) {

        console.log('submitSuccess');
        //we must call resolve method in V6.
        actions.resolve({ resultCode: 'Authorised' });
        isSubmitting = false;

    }

    function submitCancel(response) {

        console.log('submitCancel fired logic 2');


        window.Opf.payments.checkout.showInfoMessage('Payment has been cancelled!', 12000);
        isSubmitting = false;


    }

    function submitPending(response, actions) {
        console.log('submitPending');
        if (response && response.customFields && response.customFields.length > 0) {
            //retrive the resultCode and action parameters which mapped from psp response
            const map = new Map(response.customFields.map(obj => [obj.key, obj.value]));

            actions.resolve({ resultCode: map.get('resultCode'), action: JSON.parse(map.get('action')) });

        } else {
            submitSuccess(response, actions);
        }
    }

    function submitFail(error, actions) {
        console.log('submitFail', error);
        actions.reject();
        isSubmitting = false;
        // Reinitialize the payment form to allow retry
        window.Opf.payments.checkout.reinitiatePaymentForm()
            .then((success) => {
                if (success) {
                    console.log('Payment form reinitialized successfully');
                    // Optionally refresh the payment form UI
                    refreshPaymentFormUI();
                }
            })
            .catch((error) => {
                console.error('Failed to reinitialize payment form:', error);
            });
    }

    function makePayment(data, actions) {
        console.log('hosted-fields makePayment');



        if (!hasDelivery && "zip" == data.paymentMethod.type) {
            data.paymentMethod.clickAndCollect = true;

        }

        console.log('clickAndCollect', data.paymentMethod.clickAndCollect);

        var returnUrl = new URL(resultURL);
        returnUrl.searchParams.set('paymentMethod', data.paymentMethod.type);

        if (data.storePaymentMethod !== undefined) {
            storePaymentMethod = data.storePaymentMethod;
        }


        if (data.paymentMethod.storedPaymentMethodId !== undefined) {
            //storedPaymentMethodId = true;
            storedPaymentMethodId = data.paymentMethod.storedPaymentMethodId;
            console.log('storedPaymentMethodId', storedPaymentMethodId);
        }

        const pm = data?.paymentMethod ?? {};
        const type = pm.type;
        //If you need support for additional payment methods (e.g., Google Pay), please contact the SAP #caas-bamboo team.
        const KEYS_BY_TYPE = {
            scheme: [
                'type',
                'brand',
                'holderName',
                'encryptedCardNumber',
                'encryptedExpiryMonth',
                'encryptedExpiryYear',
                'encryptedSecurityCode',
                'checkoutAttemptId'
            ],
            applepay: [
                'type',
                'applePayToken',
                'checkoutAttemptId'
            ]
        };

        function addIfDefined(arr, key, value) {
            if (value !== undefined && value !== null && value !== '') {
                arr.push({ key, value });
            }
        }

        let additionalData = [];

        const selectedKeys = KEYS_BY_TYPE[type];

        for (const k of selectedKeys) {
            addIfDefined(additionalData, k, pm[k]);
        }


        additionalData.push(
            { key: 'returnUrl', value: returnUrl.href },
            { key: 'originUrl', value: location.origin },
            //state.data.storePaymentMethod
            { key: 'storePaymentMethod', value: storePaymentMethod },
            { key: 'storedPaymentMethodId', value: storedPaymentMethodId },
            { key: 'isApplepay', value: type === 'applepay' },
            { key: 'isCard', value: type === 'scheme' }
        );

        var submitBody = {
            'additionalData': additionalData,
            'submitSuccess': function (response) {
                submitSuccess(response, actions);
            },
            'submitPending': function (response) {
                submitPending(response, actions);
            },
            'submitFailure': function (error) {
                submitFail(error, actions);
            },
            'submitCancel': function (error) {
                submitCancel(error, actions);
            }
        }
        console.log('before submit');

        return window.Opf.payments.checkout.submit(submitBody);
    }

    function makeDetailsCall(data, actions) {
        console.log('hosted-fields makeDetailsCall');

        var additionalData = [
            { 'key': 'paymentData', 'value': data.paymentData || '' },
            { 'key': 'threeDSResult', 'value': data.details.threeDSResult }
        ];
        var submitCompleteBody = {
            'additionalData': additionalData,
            'submitSuccess': function (response) {
                submitSuccess(response, actions);
            },
            'submitPending': function (response) {
                submitPending(response, actions);
            },
            'submitFailure': function (error) {
                submitFail(error, actions);
            },
            'submitCancel': function (error) {
                submitCancel(error, actions);
            }
        }
        console.log('before submitComplete');
        isSubmitting = false;

        return window.Opf.payments.checkout.submitComplete(submitCompleteBody);
    }

    const configuration = {

        paymentMethodsResponse: paymentMethodsResponse, // The `/paymentMethods` response from the server.
        clientKey: clientKey,
        locale: contentLanguage,
        environment: environment,
        countryCode: countryCode,
        //showPayButton: false,//call .submit flow on your own button

        amount: {
            value: amountInExponent,
            currency: currency
        },

        onSubmit: (state, component, actions) => {
            // Your function calling your server to make the `/payments` request
            console.log('onSubmit');
            makePayment(state.data, actions);
        },

        onAdditionalDetails: (state, component, actions) => {
            // Your function calling your server to make a `/payments/details` request
            makeDetailsCall(state.data, actions);
        },
        onPaymentCompleted: (result, component) => {
            console.log('onPaymentCompleted', result);
            isSubmitting = false;
        },
        onPaymentFailed: (result, component) => {
            isSubmitting = false;

        },
        onError: (error, component) => {
            isSubmitting = false;

        }
    };



    const cardConfiguration = {
        showBrandIcon: true,
        enableStoreDetails: true,
        billingAddressRequired: false, // When true show the billing address input fields and mark them as required.
        hasHolderName: true,
        holderNameRequired: true,
        showPayButton: false,
        brands: ['mc', 'visa', 'amex', 'maestro'],
        //minimumExpiryDate: '01/26',
        amount: {
            value: amountInExponent,
            currency: currency,
        },
        placeholders: {
            cardNumber: '1234 5678 9012 3456',
            expiryDate: 'MM/YY',
            securityCodeThreeDigits: '123',
            securityCodeFourDigits: '1234',
            holderName: 'J. Smith'
        },
        onChange: (state, component) => {
            //console.log('Card state changed:', state);
        },
        onBinValue: (event) => {
            console.log('update to your OnBinValue log and remove this log  :', event.binValue);
        },
        onBrand: (event) => {
            console.log('update to your onBrand log and remove this log  :', event.brand);
        },
        onBinLookup: (event) => {
            console.log('update to your onBinLookup log and remove this log  :', event.supportedBrands);
        },
        onFieldValid: (event) => {
            console.log('update to your onFieldValid log and remove this log  :', event.valid);
        },

    };

    const applePayConfiguration = {
        countryCode: countryCode,
        isExpress: true,
        //requiredBillingContactFields: ['postalAddress'],
        //requiredShippingContactFields: ['postalAddress', 'name', 'phoneticName', 'phone', 'email'],
        onAuthorized: (data, actions) => { //get shopper's info ,trigger before onSubmit
            console.log('Authorized event containing raw data and pre-formatted data', data);
            actions.resolve();
        }
    };


    //const card component;
    let card;
    let applepay;

    const adyenCheckout = AdyenCheckout(configuration);
    adyenCheckout.then(data => {
        card = new Card(data, cardConfiguration).mount('#card-container');
        //disabled applepay by default
        //applepay = new ApplePay(data, applePayConfiguration).mount('#applepay-container');
    });

    //
    const payButton = document.getElementById('custom-pay-button');
    payButton.addEventListener('click', (event) => {
        event.preventDefault();

        if (!card) {
            console.log("!card isSubmitting ", isSubmitting);
            isSubmitting = false;
            return;
        }

        if (isSubmitting) {
            console.log(" isSubmitting ", isSubmitting);
            return;
        }

        isSubmitting = true;
        const submitPromise = card.submit();

        if (submitPromise && typeof submitPromise.then === 'function') {
            submitPromise
                .then((result) => {
                    console.log('Card submit initiated successfully');
                })
                .catch((error) => {
                    console.error('Card submit error:', error);
                    isSubmitting = false;


                });
        } else {

            isSubmitting = false;
        }
    });


})();
