  let submitButton = document.getElementById('submit-button');


  function changeImpactedElement(tagId, removeClass, addClass) {
    removeClass = removeClass || "";
    addClass = addClass || "";


    var elements = document.querySelectorAll('[data-bluesnap="' + tagId + '"]');
    elements.forEach(function (element) {
      if (removeClass) {
        removeClass.split(/\s+/).forEach(function (c) {
          if (c) element.classList.remove(c);
        });
      }
      if (addClass) {
        addClass.split(/\s+/).forEach(function (c) {
          if (c) element.classList.add(c);
        });
      }
    });
  }

  function keepAfter(target, delimiter) {
    var index = target.indexOf(delimiter);
    if (index !== -1) {
      return target.substring(index + delimiter.length);
    }
    return '';
  }

