    "VISA": "https://files.readme.io/9018c4f-Visa.png"
  };


  var sessionToken = keepAfter(OpfContext.customFields.locationElement, 'payment-fields-tokens/');


  console.log('sessionToken===>:', sessionToken);
  var bsObj = {
    token: sessionToken,
    '3DS': OpfContext.vars.allow3DS2,
    onFieldEventHandler: {
      onFocus: function (tagId) {
        console.log('onFocustageid===>:', tagId);
        changeImpactedElement(tagId, "hosted-field-valid hosted-field-invalid", "hosted-field-focus");
      },
      onBlur: function (tagId) {
        console.log('onBlurtageid===>:', tagId);
        changeImpactedElement(tagId, "hosted-field-focus");
      },
      onError: function (tagId, errorCode, errorDescription, eventOrigin) {
        changeImpactedElement(tagId, "hosted-field-valid hosted-field-focus", "hosted-field-invalid");
        const helpElement = document.getElementById(tagId + "-help");
        if (helpElement) {
          helpElement.classList.remove('helper-text-green');
          helpElement.textContent = errorCode + " - " + errorDescription + " - " + eventOrigin;
          submitButton.disabled = false;
        }
      },
      onType: function (tagId, cardType, cardData) {
        const cardLogoImg = document.querySelector("#card-logo > img");
        if (cardLogoImg) {
          cardLogoImg.setAttribute("src", cardUrl[cardType]);
        }
      },
      onValid: function (tagId) {
        console.log('onVlaidtageid===>:', tagId);
        changeImpactedElement(tagId, "hosted-field-focus hosted-field-invalid", "hosted-field-valid");
        const helpElement = document.getElementById(tagId + "-help");
        if (helpElement) {
          helpElement.textContent = "";
        }
      }
    },
    style: {
      "input": {
        "font-size": "14px",
        "font-family": "Helvetica Neue,Helvetica,Arial,sans-serif",
        "line-height": "1.42857143",
        "color": "#555"
      },
      ":focus": {
        "color": "#555"
      }
    },
    ccnPlaceHolder: "4111111111111111",
    cvvPlaceHolder: "737",
    expPlaceHolder: "03 / 30"
  };



 console.log("OpfContext.amount",OpfContext.amount);
  var threeDSecureObj = {
    authenticationIndicator: OpfContext.vars.threeDSIndicator,
    amount: Number(OpfContext.amount),
    currency: OpfContext.currency,
    billingAddress: OpfContext.billingAddress.addressLine1,
    billingCity: OpfContext.billingAddress.city,
    billingCountry: OpfContext.billingAddress.country,
    billingZip: OpfContext.billingAddress.postalCode,
    billingState: OpfContext.billingAddress.state,
    email: OpfContext.emailAddress,
    billingFirstName: OpfContext.billingAddress.firstName,
