    phone: OpfContext.billingAddress.phoneNumber
  };

  console.log('phone:', OpfContext.billingAddress.phoneNumber);

  bluesnap.hostedPaymentFieldsCreate(bsObj);

  console.log("create...payment fields");

  function makePayment() {
    console.log('hosted-fields makePayment');

    var additionalData = [
      { 'key': 'sessionToken', 'value': sessionToken },
    ];
    var submitBody = {
      'additionalData': additionalData
    }
    return window.Opf.payments.checkout.submit(submitBody);
  }


  var includeTreeDsObj = OpfContext.vars.allow3DS2;

  function submitDataCallback(callback) {

    console.log("callback...", callback);
    if (null != callback.error) {
      var errorArray = callback.error;

      for (var i = 0; i < errorArray.length; i++) {
        const helpElement = document.getElementById(errorArray[i].tagId + "-help");
        if (helpElement) {
          helpElement.textContent = errorArray[i].errorCode + " - " + errorArray[i].errorDescription;
          console.log("herere--error", errorArray[i].errorCode + " - " + errorArray[i].errorDescription);
        }
      }
      submitButton.disabled = false;
    } else {

      makePayment();

    }
  }

  const submitArgs = [submitDataCallback];

  if (includeTreeDsObj) {
    submitArgs.push(threeDSecureObj);
  }

  function do_when_clicking_submit_button() {
    bluesnap.hostedPaymentFieldsSubmitData(...submitArgs);

  }
  document.getElementById('checkout-form').addEventListener('submit', function (event) {
    event.preventDefault();
    submitButton.disabled = true;
    do_when_clicking_submit_button();
  });
})();
