<script>
    const paymentSession = JSON.parse('${functions.jsEncode(input.responseBody)}'); 


    function submitSuccess(response) {
        console.log('submitSuccess');
    }

    function submitPending(response) {
        console.log('submitPending');
    }

    function submitFail(error) {
        console.log('submitFail : failed');
    }

    function makePayment(paymentId) {
       console.log('makePayment');
        var additionalData = [
            {key: 'paymentId', value: paymentId}
        ];

        var submitBody = {
            additionalData: additionalData,
            submitSuccess: submitSuccess,
            submitPending: submitPending,
            submitFailure: submitFail,
        };

        return window.Opf.payments.checkout.submit(submitBody);
    }


    async function initializeCheckout() {
        try {
            const checkout = await CheckoutWebComponents({
                publicKey: '${functions.jsEncode(vars.apiKey)}',
                environment: '${functions.jsEncode(vars.sdkEnvironment)}', // Use 'production' for Production environment
                paymentSession: paymentSession,
                onReady: () => {
                    console.log("onReady");
                },
                onSubmit(component){
                  console.log("onSubmit === Component", component)
                },
                onPaymentCompleted: (_component, paymentResponse) => {
                    console.log("Create Payment with PaymentId: ", paymentResponse.id);
                    makePayment(paymentResponse.id);
                },
                onChange: (component) => {
                  console.log("onChange === Component", component.type);
                },
                onError: (component, error) => {
                    console.log("onError", error, "Component", component);
                  if(component.type === "klarna") {
                    console.log("Klarna component error, trying to submit again");
                    component.submit();
                  }
                },
            });

            console.log("testing checkout", checkout);

            const flowComponent = checkout.create('flow');
            flowComponent.mount('#flow-container');


        } catch (error) {
            console.error("Error in checkout initialization:", error);
        }
    }

    initializeCheckout();
