<script>

function setDisableElements(_status) {
    var elements = document.querySelectorAll('.disableOnSubmit');
    elements.forEach(function(element) {
        element.disabled = _status;
    });
}

function displayResultsCard(_message, _response, _sandboxMode) {
    setDisableElements(false);
    var responseCard = document.getElementById('responseCard');
    responseCard.textContent = _message + (_response !== null ? ' ' + JSON.stringify(_response) : '');
}

function successCard(_responseData) {

    var tokenId=_responseData.data.id;
    //displayResultsCard('Request proceessed successfully!');
    makePayment(tokenId);

}

function errorCard(_errorResponseData){
    displayResultsCard('An error has been ocurred: ', _errorResponseData);
}  

function validate() {

  	var _number = document.getElementById('cardNumber').value;
    var _cvc = document.getElementById('cvv2').value;
    var _month = document.getElementById('expirationMonth').value;
    var _year = document.getElementById('expirationYear').value;

    if (!OpenPay.card.validateCardNumber(_number)) {
        displayResultsCard('An error has occurred: wrong number ', _number);
        return false;
    }

    var _type = OpenPay.card.cardType(_number);

    if (_type !== '') {
       console.log('[' + _type.toUpperCase() + ']');
    } else {
        console.log( 'UNKNOWN');
        displayResultsCard('An error has occurred: UNKNOWN Card Type ');
        return false;
    }


    if (!OpenPay.card.validateAcceptCardNumber(_number)) {
        displayResultsCard('An error has occurred: invalid number ', _number);
        return false;
    }

    if (!OpenPay.card.validateCVC(_cvc)) {
        displayResultsCard('An error has occurred: invalid cvv ', _cvc);
        return false;
    }

    if (!OpenPay.card.validateExpiry(_month, _year)) {
        displayResultsCard('An error has occurred: invalid year/Month ');
        return false;
    }

    setDisableElements(false);
    return true;

} 

function makePayment(tokenId) {
        var deviceDataId = OpenPay.deviceData.setup("processCard");
        console.log('deviceDataId',deviceDataId);
        var additionalData = [{'key': 'deviceDataId', 'value': deviceDataId },
                              {'key': 'tokenId', 'value': tokenId }
                             ];
        var submitBody = {
            'additionalData': additionalData,
             'submitSuccess': function(response){
             console.log('submitSuccess');
         },
         'submitPending': function(response){
              console.log('submitPending');
         },
          'submitFailure': function(response){
               console.log('submitFailure');
         },
           'submitCancel': function(response){
             window.location.href = '/checkout/opf-payment-and-review';
         }

            }
            return window.Opf.payments.checkout.submit(submitBody);
        }

function callDirectCreateToken() {
     var _dataCard = '';

    setDisableElements(true);

    var responseCard = document.getElementById('responseCard');
    responseCard.textContent = '';


    // 
    OpenPay.setSandboxMode(${functions.jsEncode(vars.sandboxMode)});
    OpenPay.setDevelopMode(false);

    _dataCard = document.getElementById('processCard');



    try {
        OpenPay.setId('${functions.jsEncode(input.merchantId)}');

  		OpenPay.setApiKey('${functions.jsEncode(vars.publicKey)}');


        if (validate()) {

            OpenPay.token.extractFormAndCreate(_dataCard, successCard, errorCard);
        } else {
            //displayResultsCard('Validation failed. Please correct the errors.');
        }
    } catch (e) {
        setDisableElements(true);
        displayResultsCard('Request failed (invalid data)', null);
    }
}

document.getElementById('processCard').addEventListener('click', function (e) {
    if (e.target && e.target.id == 'makeRequestCard') {
        e.preventDefault(); 
        callDirectCreateToken();
