         formBackground: 'darkgray',
         formBorderColor: 'green',
         fontColor: '#020202',
         fontSize: '18px',
         btnfcolor: '#d0c0d0',
        // btntext: 'Submit'
     };

     iframePost(target, params);
     document.getElementById("utacontainer").style.display = "block";
   }

   if (document.readyState === 'loading') {
     document.addEventListener('DOMContentLoaded', initializePayment);
   } else {
     console.log('DOM already loaded, executing immediately');
     initializePayment();
   }

   function iframePost(target, data) {
    var url = mainPath+"/HostedIframe/ACHIframe.aspx";
    var form = document.getElementById("form1");


    // Set form attributes
    form.setAttribute("target", target);
    form.setAttribute("action", url);
   form.setAttribute("method", "POST");

    // Clear existing hidden inputs (prevents duplicates on multiple calls)
    [...form.querySelectorAll('input[type="hidden"]')].forEach(el => el.remove());

    // Append new hidden inputs
    for (let [name, value] of Object.entries(data)) {
        let input = document.createElement("input");
        input.type = "hidden";
        input.name = name;
        input.value = value;
        form.appendChild(input);
    }
    // Remove elements with class 'aspNetHidden'
    [...document.querySelectorAll('.aspNetHidden')].forEach(el => el.remove());

    // Submit the form
    form.submit();
   }

</script>
