</form>","jsContent":"
var hostedTokenizationUrl = OpfContext.redirectUrl;


hostedTokenizationUrl = JSON.parse('"' + hostedTokenizationUrl.replace(/"/g, '\"') + '"');


var tokenizer = new Tokenizer(hostedTokenizationUrl, 'div-hosted-tokenization', { hideCardholderName: false, storePermanently: true });


function makePayment(hostedTokenizationId) {
  var additionalData = [{ 'key': 'hostedTokenizationId', 'value': hostedTokenizationId }];
  var submitBody = {
    'additionalData': additionalData,
    'submitSuccess': function (response) {
      console.log('submitSuccess');
    },
    'submitPending': function (response) {
      console.log('submitPending');

      if (response && response.customFields && response.customFields.length > 0) {
        //retrive the resultCode and action parameters which mapped from psp response
        const map = new Map(response.customFields.map(obj => [obj.key, obj.value]));
        var redirectDataURL=map.get('redirectDataURL');
        console.log('redirectDataURL',redirectDataURL);
        window.location.href = redirectDataURL;
    }
    },
    'submitFailure': function (response) {
      console.log('submitFailure');
    },
    'submitCancel': function (response) {
      window.location.href = '/checkout/opf-payment-and-review';
    }
  };
  window.Opf.payments.checkout.submit(submitBody);
}

// Invoking tokenizer.initialize() will add the <iframe> inside the previously referenced <div> element
tokenizer.initialize().then(() => {
  console.log('tokenizer.initialize(): ');

  var existingButton = document.getElementById('payment-button');
  if (existingButton) {
    existingButton.remove();
  }

  // construct button
  var btn = document.createElement('button');
  btn.type = 'submit';
  btn.id = 'payment-button';
  btn.textContent = 'Submit';
  document.getElementById('payone-form').appendChild(btn);

}).catch(reason => {
  // Handle iFrame load error
});

document.getElementById('payone-form').addEventListener('submit', function (e) {
  e.preventDefault();

  tokenizer.submitTokenization().then((result) => {
    if (result.success) {
      console.log('success:', result.hostedTokenizationId);
      makePayment(result.hostedTokenizationId);
    } else {
      console.error('failure:', result.error.message);
    }
  });
});



